#!/bin/bash

# Test script to verify the generated kubeconfig works
echo "🧪 Testing the generated kubeconfig..."

# Save the base64 kubeconfig to a temp file for testing
echo "" | base64 -d > /tmp/test-kubeconfig

export KUBECONFIG=/tmp/test-kubeconfig

echo "Testing connection with limited service account..."
kubectl get nodes 2>/dev/null && echo "✅ Can connect to cluster" || echo "❌ Cannot connect to cluster"

echo "Testing namespace access..."
kubectl get pods -n analytics-platform 2>/dev/null && echo "✅ Can access analytics-platform namespace" || echo "ℹ️  No pods in analytics-platform namespace (this is expected)"

echo "Testing secret creation permissions..."
kubectl create secret generic test-secret --from-literal=test=value -n analytics-platform --dry-run=client -o yaml > /dev/null && echo "✅ Can create secrets" || echo "❌ Cannot create secrets"

echo "Testing deployment permissions..."
kubectl get deployments -n analytics-platform 2>/dev/null && echo "✅ Can list deployments" || echo "ℹ️  No deployments in analytics-platform namespace (this is expected)"

echo "Testing cluster-level access (should fail)..."
kubectl get namespaces 2>/dev/null && echo "❌ WARNING: Has cluster-level access" || echo "✅ Correctly restricted to namespace"

# Cleanup
rm /tmp/test-kubeconfig
export KUBECONFIG=$HOME/.kube/config

echo "🎯 Test completed!"
